       numberBlogs:4
   })
}
paginationClick2=(num)=>{
    this.setState({
        ...this.state,
        pageno:2,
        numberBlogs:4
    })

}
    render() {

        return (
            <div className="pagination">
            <ul style={{color:'#fff'}}>
                <li><a onClick={this.paginationClick1}>1</a></li>
                <li><a onClick={this.paginationClick2}>2</a></li>

            </ul>
        </div>
        )
    }
}
