import React, { Component } from 'react'

class DragDrop extends Component {
    state = {
        tasks: [
            {name:"1",category:"row1", bgcolor: "yellow"},
            {name:"2", category:"row2", bgcolor:"pink"},
            {name:"3", category:"row3", bgcolor:"red"},
            {name:"5", category:"row4", bgcolor:"skyblue"},
            {name:"6", category:"row5", bgcolor:"red"},
            {name:"7", category:"row6", bgcolor:"skyblue"},
            {name:"8", category:"row7", bgcolor:"red"},
            {name:"4", category:"row8", bgcolor:"skyblue"},
            {name:"9", category:"row9", bgcolor:"skyblue"}

          ]
    }

    onDragStart = (ev, id) => {
        console.log('dragstart:',id);
        ev.dataTransfer.setData("id", id);
    }

    onDragOver = (ev) => {
        ev.preventDefault();
    }

    onDrop = (ev, cat) => {
       let id = ev.dataTransfer.getData("id");

       let tasks = this.state.tasks.filter((task) => {
           if (task.name == id) {
               task.category = cat;
           }
           return task;
       });

       this.setState({
           ...this.state,
           tasks
       });
    }

    render() {
        var tasks = {
            row1: [],
            row2: [],
            row3:[],
            row4: [],
            row5: [],
            row6:[],
            row7: [],
            row8: [],
            row9:[]
        }

        this.state.tasks.forEach ((t) => {
            tasks[t.category].push(
                <div key={t.name} 
                    onDragStart = {(e) => this.onDragStart(e, t.name)}
                    draggable
                    className="draggable"
                    style = {{backgroundColor: t.bgcolor}}
                >
                    {t.name}
                </div>
            );
        });

        return (
            <div className="container-drag">




            </div>




        );
    }

}
export default DragDrop
