import React, { Component } from 'react'
import { NavLink } from "react-router-dom";
import Toggle from './toggle'

class navbar extends Component {
    state={
        isdisplay:false,
    }
    ToggleClick = () => {
        this.setState((prevState) => {
          return { isdisplay: !prevState.isdisplay };
        });
      };
    render() {
        const mainClasses="nav-header";
        if (this.state.isdisplay){
            mainClasses="nav-header is-visible";
        }
        console.log(this.state.isdisplay)

        return (

                <nav className={mainClasses}>
                    <Toggle toggleClick={this.ToggleClick}/>
                    <div className="nav-inner">
                        <div className="nav-img">
                           <a><img src="images/shu.jpeg"></img></a> 
                        </div>
                        <ul className="nav-main">
                            <li >
                                <NavLink to="/" >
                                <a className="active"> Home</a>
                                </NavLink>

                            </li>
                            <li>
                                <NavLink to="/about">
                                    <a>About</a>
                                </NavLink>
                            </li>
                            <li>
                                <NavLink to="/resume" >
                                <a>Resume</a>
                                </NavLink>
                            </li>
                            <li>
                                <NavLink to="/portfolio" >
                                <a>Portfolio</a>
                                </NavLink>
                            </li>
                            <li>
                                <NavLink to="/contact" >
                                <a>contact</a>
                                </NavLink>
                            </li>

                        </ul>
                        <p className="nav-copy"></p>
                    </div>
                </nav>


        )
    }
}
export default navbar
