import React, { Component } from 'react'

class DragDrop extends Component {
    state = {
        tasks: [
            {name:"1",category:"row1", bgcolor: "yellow"},
            {name:"2", category:"row2", bgcolor:"pink"},
            {name:"3", category:"row3", bgcolor:"skyblue"},
            {name:"5", category:"row3", bgcolor:"skyblue"},
            {name:"6", category:"row2", bgcolor:"skyblue"},
            {name:"7", category:"row2", bgcolor:"skyblue"},
            {name:"8", category:"row2", bgcolor:"skyblue"},
            {name:"4", category:"row1", bgcolor:"skyblue"},
            {name:"9", category:"row1", bgcolor:"skyblue"}

          ]
    }

    onDragStart = (ev, id) => {
        console.log('dragstart:',id);
        ev.dataTransfer.setData("id", id);
    }

    onDragOver = (ev) => {
        ev.preventDefault();
    }

    onDrop = (ev, cat) => {
       let id = ev.dataTransfer.getData("id");

       let tasks = this.state.tasks.filter((task) => {
           if (task.name == id) {
               task.category = cat;
           }
           return task;
       });

       this.setState({
           ...this.state,
           tasks
       });
    }

    render() {
        var tasks = {
            wip: [],
            complete: []
        }

        this.state.tasks.forEach ((t) => {
            tasks[t.category].push(
                <div key={t.name} 
                    onDragStart = {(e) => this.onDragStart(e, t.name)}
                    draggable
                    className="draggable"
                    style = {{backgroundColor: t.bgcolor}}
                >
                    {t.name}
                </div>
            );
        });

        return (
            <div className="container-drag">
                <div className="row">
                    <div className="col-md-4">
                        <div className="row">
                            <div className="col-md-4">
                                <div className="r-first"
                                    onDragOver={(e)=>this.onDragOver(e)}
                                    onDrop={(e)=>{this.onDrop(e, "wip")}}>
                                    <span className="task-header">WIP</span>
                                    {tasks.wip}
                                </div>
                            </div>
                            <div className="col-md-4">
                                <div className="r-first"
                                    onDragOver={(e)=>this.onDragOver(e)}
                                    onDrop={(e)=>{this.onDrop(e, "wip")}}>
                                    <span className="task-header">WIP</span>
                                    {tasks.wip}
                                </div>
                            </div>
                            <div className="col-md-4">
                                <div className="r-first"
                                    onDragOver={(e)=>this.onDragOver(e)}
                                    onDrop={(e)=>{this.onDrop(e, "wip")}}>
                                    <span className="task-header">WIP</span>
                                    {tasks.wip}
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-md-4">
                                <div className="r-first"
                                    onDragOver={(e)=>this.onDragOver(e)}
                                    onDrop={(e)=>{this.onDrop(e, "wip")}}>
                                    <span className="task-header">WIP</span>
                                    {tasks.wip}
                                </div>
                            </div>
                            <div className="col-md-4">
                                <div className="r-first"
                                    onDragOver={(e)=>this.onDragOver(e)}
                                    onDrop={(e)=>{this.onDrop(e, "wip")}}>
                                    <span className="task-header">WIP</span>
                                    {tasks.wip}
                                </div>
                            </div>
                            <div className="col-md-4">
                                <div className="r-first"
                                    onDragOver={(e)=>this.onDragOver(e)}
                                    onDrop={(e)=>{this.onDrop(e, "wip")}}>
                                    <span className="task-header">WIP</span>
                                    {tasks.wip}
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-md-4">
                                <div className="r-first"
                                    onDragOver={(e)=>this.onDragOver(e)}
                                    onDrop={(e)=>{this.onDrop(e, "wip")}}>
                                    <span className="task-header">WIP</span>
                                    {tasks.wip}
                                </div>
                            </div>
                            <div className="col-md-4">
                                <div className="r-first"
                                    onDragOver={(e)=>this.onDragOver(e)}
                                    onDrop={(e)=>{this.onDrop(e, "wip")}}>
                                    <span className="task-header">WIP</span>
                                    {tasks.wip}
                                </div>
                            </div>
                            <div className="col-md-4">
                                <div className="r-first"
                                    onDragOver={(e)=>this.onDragOver(e)}
                                    onDrop={(e)=>{this.onDrop(e, "wip")}}>
                                    <span className="task-header">WIP</span>
                                    {tasks.wip}
                                </div>
                            </div>
                        </div>



                    </div>
                </div>
            </div>
        );
    }

}
export default DragDrop
