import React, { Component } from 'react'

class DragDrop extends Component {
    state = {
        tasks: [
            {name:"1",category:"r1", bgcolor: "yellow"},
            {name:"2", category:"r2", bgcolor:"pink"},
            {name:"3", category:"r3", bgcolor:"skyblue"},
            {name:"4",category:"r4", bgcolor: "yellow"},
            {name:"5", category:"r4", bgcolor:"pink"},
            {name:"6", category:"r5", bgcolor:"skyblue"},
            {name:"7",category:"r6", bgcolor: "yellow"},
            {name:"8", category:"r7", bgcolor:"pink"},
            {name:"9", category:"r8", bgcolor:"skyblue"}
          ]
    }

    onDragStart = (ev, id) => {
        console.log('dragstart:',id);
        ev.dataTransfer.setData("id", id);
    }

    onDragOver = (ev) => {
        ev.preventDefault();
    }

    onDrop = (ev, cat) => {
       let id = ev.dataTransfer.getData("id");

       let tasks = this.state.tasks.filter((task) => {
           if (task.name == id) {
               task.category = cat;
           }
           return task;
       });

       this.setState({
           ...this.state,
           tasks
       });
    }

    render() {
        var tasks = {
            r1: [],
            r2: [],
            r3: [],
            r4: [],
            r5: [],
            r6: [],
            r7: [],
            r8: [],
            r9: []
        }

        this.state.tasks.forEach ((t) => {
            tasks[t.category].push(
                <div key={t.name} 
                    onDragStart = {(e) => this.onDragStart(e, t.name)}
                    draggable
                    className="draggable"
                    style = {{backgroundColor: t.bgcolor}}
                >
                    {t.name}
                </div>
            );
        });

        return (
            <div className="container-drag">
                <h2 className="header">DRAG & DROP DEMO</h2>
                <div className="wip"
                    onDragOver={(e)=>this.onDragOver(e)}
                    onDrop={(e)=>{this.onDrop(e, "wip")}}>
                    <span className="task-header">WIP</span>
                    {tasks.wip}
                </div>
                <div className="droppable" 
                    onDragOver={(e)=>this.onDragOver(e)}
                    onDrop={(e)=>this.onDrop(e, "complete")}>
                     <span className="task-header">COMPLETED</span>
                     {tasks.complete}
                </div>


            </div>
        );
    }
}
export default DragDrop
